import React, { useMemo } from 'react'

import classNames from 'classnames'

import { ThemeProps } from '@sourcegraph/shared/src/theme'
import { Link } from '@sourcegraph/wildcard'

import styles from './CustomersSection.module.scss'

export const CustomersSection: React.FunctionComponent<React.PropsWithChildren<ThemeProps>> = props => {
    const logos = useMemo(
        () => [
            <DropboxLogo key="dropbox-logo" {...props} />,
            <CloudflareLogo key="cloudflare-logo" {...props} />,
            <GELogo key="ge-logo" />,
            <CanvaLogo key="canva-logo" {...props} />,
            <RedditLogo key="reddit-logo" {...props} />,
            <UberLogo key="uber-logo" {...props} />,
            <YelpLogo key="yelp-logo" {...props} />,
            <LyftLogo key="lyft-logo" {...props} />,
        ],
        [props]
    )

    return (
        <>
            <div className={classNames('text-muted text-center mb-3', styles.text)}>
                Our customers use Sourcegraph every day to build software you rely on.{' '}
            </div>
            <Link
                className={styles.logos}
                to="https://about.sourcegraph.com/customers"
                aria-label="Learn more about our customers"
            >
                {logos.map(logo => (
                    <div key={logo.key} className={styles.logo}>
                        {logo}
                    </div>
                ))}
            </Link>
        </>
    )
}

