            NEW.committed_at, func_lsif_uploads_transition_columns_diff(
                func_row_to_lsif_uploads_transition_columns(OLD),
                func_row_to_lsif_uploads_transition_columns(NEW)
            )
        );

        RETURN NEW;
    END;
$$ LANGUAGE plpgsql;

