'use strict';
var env = require("vs/base/common/platform");
var pfs = require("vs/base/node/pfs");
var winjs_base_1 = require("vs/base/common/winjs.base");
exports.DEFAULT_TERMINAL_LINUX_READY = new winjs_base_1.TPromise(function (c) {
    if (env.isLinux) {
        pfs.exists('/etc/debian_version').then(function (isDebian) {
            if (isDebian) {
                c('x-terminal-emulator');
            }
            else if (process.env.DESKTOP_SESSION === 'gnome' || process.env.DESKTOP_SESSION === 'gnome-classic') {
                c('gnome-terminal');
            }
            else if (process.env.DESKTOP_SESSION === 'kde-plasma') {
                c('konsole');
            }
            else if (process.env.COLORTERM) {
                c(process.env.COLORTERM);
            }
            else if (process.env.TERM) {
                c(process.env.TERM);
            }
            else {
                c('xterm');
            }
        });
        return;
    }
    c('xterm');
});
exports.DEFAULT_TERMINAL_OSX = 'Terminal.app';
exports.DEFAULT_TERMINAL_WINDOWS = '%COMSPEC%';
